import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    todoList: [],
    index: null,
    mode: 'add',
  },
  mutations: {
    listAdd: (state, payload) => {state.todoList = payload},
    listEdit: (state, payload) => {state.index = payload},
  },
  actions: {
    listAdd: ({ commit }, payload) => {commit('listAdd',payload)},
    listEdit: ({ commit }, payload) => {commit('listEdit',payload)},
  }
})
